import Button from '@restart/ui/esm/Button';
import React from 'react';
import { Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import initializeAuthentication from '../Firebase/firebase.init';
import { useEffect, useState } from "react";
import useAuth from '../../Hooks/useAuth';
import { getAuth } from '@firebase/auth';
import './Login.css'
initializeAuthentication();
const Login = () => {
    const auth = getAuth();
    const [email, setEmail] = useState('');
    const [error, setError] = useState('');
    const [password, setPassword] = useState('');
    const { signInUsingGoogle, signInUsingGithub, loginWIthEmailPassword } = useAuth();


    // handleLogin function call 
    const handleLogin = (event) => {
        event.preventDefault();
        loginWIthEmailPassword(email, password);
    }
    // handleEmailChange function call 
    const handleEmailChange = e => {
        setEmail(e.target.value)
    }
    // handlePasswordChange function call 
    const handlePasswordChange = e => {
        setPassword(e.target.value);
    }
    return (
        <div className="container w-50 login-section">
            <h1 className="text-center">Please Login</h1>
            <Form onSubmit={handleLogin}>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control onBlur={handleEmailChange} type="email" placeholder="Enter email" required />

                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label >Password</Form.Label>
                    <Form.Control onBlur={handlePasswordChange} type="password" placeholder="Password" required />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicCheckbox">
                    <Form.Check type="checkbox" label="Save Password" />
                </Form.Group>
                <Button variant="primary" type="submit" className="regular-btn">
                    Login
                </Button>
                <hr />
                <Button onClick={signInUsingGoogle} className="regular-btn me-4">
                    Google Sign In
                </Button>

                <Button onClick={signInUsingGithub} className="regular-btn">
                    GitHub Sign In
                </Button>
                <br />
                <Link to="/signup" >
                    Need a account?
                </Link>
            </Form>
        </div >
    );
};

export default Login;
