import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router';
import { Link } from 'react-router-dom';
import { useForm } from "react-hook-form";
import axios from 'axios';
import { Card } from 'react-bootstrap';
const Booking = (props) => {
    const { bookingId, serviceId } = useParams();
    const [service, setService] = useState({});
    const [booking, setBooking] = useState({});
    useEffect(() => {
        fetch(`https://grisly-warlock-36369.herokuapp.com/services/${bookingId}`)
            .then(res => res.json())
            .then(data => setService(data))
    }, []);
    useEffect(() => {
        fetch(`https://grisly-warlock-36369.herokuapp.com/services/${bookingId}`)
            .then(res => res.json())
            .then(data => setBooking(data))
    }, []);

    const { register, handleSubmit, reset } = useForm();
    const onSubmit = data => {
        console.log(data);
        axios.post('https://grisly-warlock-36369.herokuapp.com/manageBookings', data)
            .then(res => {
                if (res.data.insertedId) {
                    alert('added successfully')
                    reset();
                }
            })
    };
    return (
        <>
            <Card className="container w-50 mt-5 text-center">
                <Card.Img variant="top" src={service.img} />
                <Card.Body>
                    <Card.Title>${service.price}</Card.Title>
                    <Card.Title>{service.destination}</Card.Title>
                    <Card.Text>
                        {service.description}
                    </Card.Text>
                </Card.Body>
            </Card>

            <form onSubmit={handleSubmit(onSubmit)}>
                <input {...register("destination", { required: true, maxLength: 20 })} placeholder="Destination Name" />
                <input type="number" {...register("price")} placeholder="Price" />
                <textarea {...register("description")} placeholder="Place description" />
                <input type="submit" />
            </form>
            <Link to='/services'>
                <button className="regular-btn">Go back</button>
            </Link>
        </>
    );
};

export default Booking;
