import { useEffect, useState } from "react";
import initializeAuthentication from "../components/Firebase/firebase.init";
import { getAuth, signInWithPopup, createUserWithEmailAndPassword, GoogleAuthProvider, GithubAuthProvider, onAuthStateChanged, signOut } from "firebase/auth";
import SignUp from "../components/SignUp/SignUp";
import useAuth from "./useAuth";

initializeAuthentication();
const useFirebase = (props) => {
    const [user, setUser] = useState({});
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(true);
    const auth = getAuth();
    const googleProvider = new GoogleAuthProvider();
    const githubProvider = new GithubAuthProvider();
    const { name, email, password } = SignUp();

    // CreateUser Using google Email & Password 
    const createUserUsingEmail = () => {
        if (password.length < 6) {
            return;
        }
        createUserWithEmailAndPassword(auth, email, password)
            .then(userCredential => {
                setUser(userCredential.user)
            })
            .catch(error => {
                setError(error.message);
            })
    }
    // SignIn Using google provider 
    const signInUsingGoogle = () => {
        setIsLoading(true);
        signInWithPopup(auth, googleProvider)
            .then(result => {
                setUser(result.user)
            })
            .catch(error => {
                setError(error.message);
            })
            .finally(() => setIsLoading(false));
    }

    //Sign In using Github Provider
    const signInUsingGithub = () => {
        signInWithPopup(auth, githubProvider)
            .then(result => {
                setUser(result.user)
            })
            .catch(error => {
                setError(error.message);
            })
            .finally(() => setIsLoading(false));
    }
    // logout function call 
    const logout = () => {
        setIsLoading(true);
        signOut(auth)
            .then(() => {
                setUser({});
            })
            .finally(() => setIsLoading(false))
    }
    // observe user state change 
    useEffect(() => {
        const unsubscribed = onAuthStateChanged(auth, user => {
            if (user) {
                setUser(user)
            }
            else {
                setUser({});
            }
            setIsLoading(false)
        });
        return () => unsubscribed;
    }, [])
    return {
        user,
        error,
        isLoading,
        createUserUsingEmail,
        signInUsingGoogle,
        signInUsingGithub,
        logout
    }
}

export default useFirebase;
