import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router';
import { Link } from 'react-router-dom';
import { useForm } from "react-hook-form";
import axios from 'axios';
const ServiceDetails = (props) => {
    const { serviceId } = useParams();
    const [service, setService] = useState({});
    useEffect(() => {
        fetch(`https://grisly-warlock-36369.herokuapp.com/services/${serviceId}`)
            .then(res => res.json())
            .then(data => setService(data))
    }, [])
    const { register, handleSubmit, reset } = useForm();
    const onSubmit = data => {
        console.log(data);
        axios.post('https://grisly-warlock-36369.herokuapp.com/services', data)
            .then(res => {
                if (res.data.insertedId) {
                    alert('added successfully')
                    reset();
                }
            })
    };
    return (
        <>
            <div className="text-center">
                <h1> {service.destination}</h1>
                <img src={service.img} alt="" />
                <p>{service.description}</p>
            </div>
            <form onSubmit={handleSubmit(onSubmit)}>
                <input {...register("destination", { required: true, maxLength: 20 })} placeholder="Destination Name" />
                <input type="number" {...register("price")} placeholder="Price" />
                <textarea {...register("description")} placeholder="Place description" />
                <input type="submit" />
            </form>
            <Link to='/services'>
                <button className="regular-btn">Go back</button>
            </Link>
        </>
    );
};

export default ServiceDetails;
