import Service from "../Service/Service";


const ManageAllBookings = () => {
    const [bookings, setBookings] = useState([]);
    console.log(bookings);
    useEffect(() => {
        fetch('http://localhost:5000/manageBookings')
            .then(res => res.json())
            .then(data => setBookings(data))
